def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def get_primes(n):
    primes = []
    i = 2
    while len(primes) < n:
        if is_prime(i):
            primes.append(i)
        i += 1
    return primes

def primes_to_binary(primes):
    return [bin(prime).replace("0b", "") for prime in primes]

def print_primes(n, binary=False):
    primes = get_primes(n)
    primes.reverse()
    if binary:
        primes = primes_to_binary(primes)
    for prime in primes:
        print(prime)

# Printing first 100 prime numbers in reverse order
